import sys

def main():
    q = int(sys.stdin.readline())
    for i in range(q):
        n, a, b = map(int, sys.stdin.readline().split())
        if a <= b / 2:
            print(a * (n // 2) + b * (n % 2))
        else:
            print(b * ((n + 1) // 2))

main()

