import sys

def shiritori(N, W_i):
    list_W_i = []
    for i in range(N):
        list_W_i.append(W_i[i])
    for i in range(N-1):
        if list_W_i[i] == list_W_i[i+1]:
            return "No"
        elif list_W_i[i][-1] != list_W_i[i+1][0]:
            return "No"
    return "Yes"

def main():
    N = int(sys.stdin.readline())
    W_i = []
    for i in range(N):
        W_i.append(sys.stdin.readline()[:-1])
    print(shiritori(N, W_i))

if __name__ == '__main__':
    main()
