import sys

def solve(n):
    count = 0
    while n > 0:
        if n % 6 == 0:
            n -= 6
        elif n % 9 == 0:
            n -= 9
        else:
            n -= 1
        count += 1
    print(count)

if __name__ == '__main__':
    n = int(sys.stdin.readline())
    solve(n)

