import sys


def withdraw(n):
    if n == 1:
        return 1
    if n % 6 == 0:
        return min(withdraw(n // 6) + 1, withdraw(n - 1) + 1)
    if n % 9 == 0:
        return min(withdraw(n // 9) + 1, withdraw(n - 1) + 1)
    return withdraw(n - 1) + 1


def main():
    n = int(sys.stdin.readline().strip())
    # n = 127
    print(withdraw(n))
