def main():
    N = int(input())
    x = 0
    i = 1
    j = 1
    while N > 0:
        if N >= 6 ** i:
            i += 1
            continue
        else:
            N -= 6 ** (i - 1)
            x += 1
        if N >= 9 ** j:
            j += 1
            continue
        else:
            N -= 9 ** (j - 1)
            x += 1
    print(x)

