import sys

def subsequence(s, t):
    m = len(s)
    n = len(t)
    l = [[0 for x in range(n+1)] for x in range(m+1)]
    for i in range(m+1):
        for j in range(n+1):
            if i == 0 or j == 0:
                l[i][j] = 0
            elif s[i-1] == t[j-1]:
                l[i][j] = l[i-1][j-1] + 1
            else:
                l[i][j] = max(l[i-1][j], l[i][j-1])
    return l[m][n]

def main():
    reader = (line.strip() for line in sys.stdin)
    s, t = next(reader), next(reader)
    print(len(s) - subsequence(s, t))

if __name__ == "__main__":
    main()

def longestCommonSubsequence(X,Y,m,n):
    L = [[0]*(n+1) for i in range(m+1)]
    for i in range(m+1):
        for j in range(n+1):
            if i == 0 or j == 0:
                L[i][j] = 0
            elif X[i-1] == Y[j-1]:
                L[i][j] = L[i-1][j-1] + 1
            else:
                L[i][j] = max(L[i-1][j],L[i][j-1])
    return L[m][n]

def main():
    X = list(input())
    Y = list(input())
    m = len(X)
    n = len(Y)
    print(m - longestCommonSubsequence(X,Y,m,n))

