import sys

def main():
    n = int(sys.stdin.readline())
    move = 0
    i = 1
    j = 1

    while i < n or j < n:
        if i == j:
            i += 1
        elif i < j:
            i += 1
        elif i > j:
            j += 1

        move += 1

    print(move)

if __name__ == "__main__":
    main()

def main():
    """
    >>> main()
    5
    """
    N = int(input())
    r, c = 1, 1

    while r * c < N:
        if r < c:
            r += 1
        else:
            c += 1
    print(r + c - 2)


if __name__ == "__main__":
    main()
