def cup_of_coffee():
    n, m = map(int, input().split())
    a = map(int, input().split())

    total_pages = 0
    for i in range(n):
        total_pages += max(0, a[i] - i)

    if total_pages < m:
        return -1

    max_cups = 0
    for i in range(n):
        max_cups += min(a[i], n - i)

    if max_cups >= m:
        return 1

    return -1


print(cup_of_coffee())

