    for i in range(1, m + 1):
        p[i] = p[i - 1] + d[i]
    ans = []

    def f(x):
        if x == 0:
            return 0
        if x == p[m]:
            return m
        l, r = 0, m
        while l < r:
            mid = (l + r) // 2
            if p[mid] < x:
                l = mid + 1
            else:
                r = mid
        return l

    now = 0
    for i in range(n):
        if a[i] > now:
            ans.append('R')
            now = a[i]
        else:
            x = f(p[now] - i)
            if x > now:
                ans.append('R')
                now = x
            else:
                ans.append('L')
    print(len(ans))
    print(''.join(ans))


