def main():
    n = int(sys.stdin.readline())
    arr = list(map(int, sys.stdin.readline().split()))
    if n == 1:
        print(0)
        return
    elif n == 2:
        if arr[0] == arr[1]:
            print(0)
        else:
            print(1)
        return
