def median(s, t):
    if s == t:
        return s

    mid = (ord(t[0]) - ord(s[0])) // 2
    if mid == 0:
        return s + median(s[1:], t[1:])
    if mid == ord(t[0]) - ord(s[0]):
        return t[0] + median(s[1:], t[1:])

    return chr(ord(s[0]) + mid) + median(s[1:], t[1:])

k = int(input())
