	for i in a:
		topics.add(i)
	topics = list(topics)
	topics.sort()
	cnt = {}
	for i in topics:
		cnt[i] = 0
	for i in a:
		cnt[i] += 1
	#print(topics)
	#print(cnt)
	ans = 0
	for i in topics:
		ans += cnt[i] - cnt[i] % 2
	return ans

