import sys
sys.setrecursionlimit(100000)

def get_input():
    inputs = []
    for i in range(4):
        inputs.append(list(map(int, input().split())))
    return inputs

def get_cards(cards, index, k):
    return cards[index*k:(index+1)*k]


def get_player_joy(cards, player_favorite_number, joy_levels, k):
    fav_cards = list(filter(lambda x: x == player_favorite_number, cards))
    return (k-len(fav_cards))*joy_levels[0] + len(fav_cards)*joy_levels[1]

def main():
    n, k = get_input()[0]
    cards = get_input()[1]
    player_favorite_numbers = get_input()[2]
    joy_levels = get_input()[3]
    max_joy = -1
    for i in range(n):
        max_joy = max(max_joy, get_player_joy(get_cards(cards, i, k), player_favorite_numbers[i], joy_levels, k))
    print(max_joy)


if __name__ == "__main__":
    main()


