t = int(input())
for _ in range(t):
    n, k = map(int, input().split())
    if k == 1:
        print(n)
        continue
    if k == 2:
        print(n//2 + n%2)
        continue
    if k >= 3:
        if n//k >= 1:
            print(n//k)
            continue
        if n//k == 0:
            print(1)
            continue

import sys

def main():
    """
    Main Function
    """
    num_cases = int(sys.stdin.readline())
    for _ in range(num_cases):
        n, k = [int(x) for x in sys.stdin.readline().split()]
        if n < k:
            print(n)
        else:
            print((n // k) * k + min(k // 2, n % k))

if __name__ == "__main__":
    main()
