class Solution:
    def reverseBits(self, n):
        # convert n to binary
        n = bin(n)[2:]
        # pad n with zeros
        n = n.zfill(32)
        # reverse the string
        n = n[::-1]
        # convert n to an integer
        return int(n, 2)
