class Solution:
    def hasCycle(self, head: ListNode) -> bool:
        if not head or not head.next:
            return False
        slow = head
        fast = head.next
        while slow != fast:
            if not fast or not fast.next:
                return False
            slow = slow.next
            fast = fast.next.next
        return True

"""
Algorithm:
1. Create 2 pointers: fast and slow
2. If fast and slow meet at same node, return True
3. If fast and slow don't meet, return False

T: O(n), S: O(1)
"""

"""
@param {ListNode} head
@return {boolean}
"""
def hasCycle(head):
    if head == None:
        return False

    fast = head.next
    slow = head

    while fast != None and fast.next != None and slow != None:
        if fast == slow:
            return True
        fast = fast.next.next
        slow = slow.next

    return False

