def main():
    n = int(input())
    ingredients = []

    for i in range(n):
        ingredients.append([int(x) for x in input().split()])

    ingredients.sort()

    minDiff = abs(ingredients[0][0] - ingredients[0][1])
    minDiffIngredient = ingredients[0]

    for i in range(1, n):
        if ingredients[i][0] > ingredients[i - 1][0]:
            diff = abs(ingredients[i][0] - ingredients[i][1])
            if diff < minDiff:
                minDiff = diff
                minDiffIngredient = ingredients[i]

    print(minDiff)

if __name__ == '__main__':
    main()

import sys

def min_diff(s, b, n, index, curr_s, curr_b, diff):
    if index == n:
        return abs(curr_s - curr_b)
    if diff < abs(curr_s - curr_b):
        return diff
    return min(min_diff(s, b, n, index + 1, curr_s, curr_b, diff),
               min_diff(s, b, n, index + 1, curr_s * s[index], curr_b + b[index], diff))

