        nums.append(int(input()))
    total = 0
    for i in nums:
        if i >= 10 and i < 100:
            total += i % 10
        elif i >= 100 and i < 1000:
            total += (i // 100) ** 2
        elif i >= 1000 and i < 10000:
            total += (i // 1000) ** 3
