def solve(white, black):
    board = [["." for i in range(8)] for j in range(8)]
    for piece in white.split(","):
        board[int(piece[2]) - 1][ord(piece[1]) - 97] = piece[0]
    for piece in black.split(","):
        board[int(piece[2]) - 1][ord(piece[1]) - 97] = piece[0]
    for line in board:
        stdout.write("+---+---+---+---+---+---+---+---+\n")
        for piece in line:
            stdout.write("|")
            if piece == ".":
                stdout.write(":::")
            else:
                stdout.write(":%s:" % piece)
        stdout.write("|\n")
    stdout.write("+---+---+---+---+---+---+---+---+\n")

