import sys

def main():
    N = int(sys.stdin.readline())
    rods = []
    for i in range(N):
        rods.append(int(sys.stdin.readline()))
    rods.sort()
    while len(rods) > 1:
        rods.append(rods.pop() + rods.pop() - 1)
        rods.sort()
    print(rods[0])

if __name__ == '__main__':
    main()

import sys

def main():
    n = int(sys.stdin.readline())
    rods = []
    for i in range(n):
        rods.append(int(sys.stdin.readline()))
    rods.sort()
    total = rods[0]
    for i in range(1,n):
        total += rods[i]-1
    print(total)

if __name__ == '__main__':
    main()

import sys

def main():
    n = int(sys.stdin.readline().strip())
    rods = []
    for i in range(n):
        rods.append(int(sys.stdin.readline().strip()))
    rods.sort()
    while len(rods) > 1:
        rods[0] += rods.pop(-1)
        rods.sort()
    print(rods[0])

if __name__ == '__main__':
    main()
