import sys

def read_ints():
    return [int(i) for i in sys.stdin.readline().strip().split(' ')]

def solve(n, preferences):
    # sort by lower bound
    preferences.sort(key=lambda pref: pref[0])

    # greedy
    rooms = 0
    for pref in preferences:
        if rooms == 0 or pref[0] > rooms:
            rooms = pref[1]

    return rooms

if __name__ == '__main__':
    n = int(sys.stdin.readline())
    preferences = [read_ints() for _ in range(n)]
    print(solve(n, preferences))

import heapq

def solve(input):
    n = int(input[0])
    intervals = [list(map(int, line.split())) for line in input[1:]]
    intervals.sort(key=lambda x: x[1])
    q = []
    for interval in intervals:
        if len(q) == 0 or q[0] > interval[0]:
            heapq.heappush(q, interval[1])
        else:
            heapq.heappop(q)
            heapq.heappush(q, interval[1])
    return len(q)


if __name__ == '__main__':
    input = [
        '3',
        '1 2',
        '2 4',
        '5 6'
    ]
    print(solve(input))

from itertools import combinations

def minion_preferences(s):
    n = int(s.readline())
    preferences = [sorted(map(int, s.readline().split())) for _ in range(n)]
    preferences.sort()
    return len(min(combinations(preferences, r) for r in range(len(preferences) + 1)), key=lambda c: max(b - a for a, b in c))

if __name__ == "__main__":
    import sys
    print(minion_preferences(sys.stdin))
