import sys

def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    for i in range(n):
        a[i] = a[i]
    s = [0] * (n + 1)
    s[0] = 0
    for i in range(1, n + 1):
        s[i] = s[i - 1] + a[i - 1]
    d = {}
    for i in range(n + 1):
        if s[i] in d:
            d[s[i]].append(i)
        else:
            d[s[i]] = [i]
    ans = 0
    for i in d:
        if len(d[i]) > 1:
            ans += len(d[i]) - 1
    print(ans)

if __name__ == '__main__':
    main()

