n = int(input())
a = list(map(int,input().split()))

def find_good_candies(n, a):
    sum_odd, sum_even = 0, 0
    for i in range(1,n):
        if i%2 == 0:
            sum_even += a[i]
        else:
            sum_odd += a[i]
    if sum_odd == sum_even:
        return 1
    else:
        return 0

ans = 0
for i in range(n):
    ans += find_good_candies(n, a[:i] + a[i+1:])

print(ans)

