# Развернуть массив

n = int(input())
a = [int(i) for i in input().split()]

for i in range(1, 5*10**8+1):
    for j in range(n):
        if a[j] == 2*i-1:
            a[j] = 2*i
        elif a[j] == 2*i:
            a[j] = 2*i-1

for i in range(n):
    print(a[i], end=' ')

def adjacent_replacements_algorithm(n, a):
    b = []
    for i in range(n):
        if a[i] % 2 == 0:
            b.append(a[i] - 1)
        else:
            b.append(a[i])
    return b


if __name__ == "__main__":
    n = int(input())
    a = list(map(int, input().split()))
    print(*adjacent_replacements_algorithm(n, a))
