#include <bits/stdc++.h>
using namespace std;

const int N = 200000 + 10;

int n, a[N];
vector<int> g[N];

void dfs(int u, int p, int &red, int &blue, int &uncolored) {
	red += (a[u] == 1);
	blue += (a[u] == 2);
	uncolored += (a[u] == 0);
	for (int v : g[u]) {
		if (v == p) continue;
		dfs(v, u, red, blue, uncolored);
	}
}

int main() {
	int ans = 0;
	cin >> n;
	for (int i = 1; i <= n; i++) {
		cin >> a[i];
	}
	for (int i = 1; i < n; i++) {
		int u, v;
		cin >> u >> v;
		g[u].push_back(v);
		g[v].push_back(u);
	}
	for (int u = 1; u <= n; u++) {
		for (int v : g[u]) {
			int red = 0, blue = 0, uncolored = 0;
			dfs(u, v, red, blue, uncolored);
			int red1 = 0, blue1 = 0, uncolored1 = 0;
			dfs(v, u, red1, blue1, uncolored1);
			if (red && blue1) continue;
			if (red1 && blue) continue;
			ans++;
		}
	}
	cout << ans << endl;
	return 0;
}

def main():
    n = int(input())
    a = list(map(int, input().split()))
    edges = []
    for i in range(n-1):
        v, u = map(int, input().split())
        edges.append((v, u))
    # n = 5
    # a = [2, 0, 0, 1, 2]
    # edges = [(1, 2), (2, 3), (2, 4), (2, 5)]
    # # n = 5
    # # a = [1, 0, 0, 0, 2]
    # # edges = [(1, 2), (2, 3), (3, 4), (4, 5)]
    # # n = 3
    # # a = [1, 1, 2]
    # # edges = [(2, 3), (1, 3)]
    nice_edges = 0
    for i in range(n-1):
        v, u = edges[i]
        if a[v-1] == 0 or a[u-1] == 0:
            nice_edges += 1
    print(nice_edges)

if __name__ == '__main__':
    main()

