            days += 2 * k[i]
        else:
            days += 1
            k[i] -= 1
            for day in on_sale:
                if k[i] > 0:
                    days += 1
                    k[i] -= 1
                else:
                    break
            if k[i] > 0:
                days += 2 * k[i]
print(days)
var fs = require('fs');

module.exports = function(file, cb) {
  fs.readFile(file, function(err, data) {
    if (err) return cb(err);
    cb(null, data);
  });
}
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

void print_file(FILE *fp)
{
    int ch;
    while ((ch = fgetc(fp)) != EOF)
    {
        putchar(ch);
    }
}

int main(int argc, char *argv[])
{
    char *fname = argv[1];
    FILE *fp;

    if (argc < 2)
    {
        fprintf(stderr, "Usage: %s filename\n", argv[0]);
        exit(1);
    }

    fp = fopen(fname, "r");
    if (fp == NULL)
    {
        printf("%s cannot be opened\n", fname);
        exit(1);
    }

    print_file(fp);
    fclose(fp);

    return 0;
}
