n, m = map(int, input().split())
a = list(map(int, input().split()))

count = [0] * m
for i in a:
    count[i % m] += 1

ans = 0
for i in range(m // 2 + 1):
    if i == 0 or i * 2 == m:
        ans += count[i] % 2
    else:
        ans += max(count[i], count[m-i]) - min(count[i], count[m-i])

print(ans)

for i in range(n):
    print(a[i] + (ans + (a[i] % m) - (n//m) * (a[i] % m)) // 2, end=' ')


from sys import stdin
from math import gcd

def read():
    return stdin.readline()

def main():
    n, m = (int(x) for x in read().split())
    a = [int(x) for x in read().split()][:n]
    a.sort()
    min_val = a[0]
    max_val = a[n-1]
    max_moves = max_val - min_val
    min_moves = 0
    for i in range(1, n+1):
        if i % m == 0:
            min_moves += a[i-1] - min_val
    if min_moves > max_moves:
        print(min_moves)
        for i in range(1, n+1):
            if i % m == 0:
                print(max_val, end=' ')
            else:
                print(min_val, end=' ')
    else:
        print(max_moves)
        for i in range(1, n+1):
            if i % m == 0:
                print(min_val, end=' ')
            else:
                print(max_val, end=' ')

main()


