T = int(input())

for _ in range(T):
    a, b = map(int, input().split())
    ans = 0
    if a < b:
        if (b - a) % 2 == 0:
            ans = 1
        else:
            ans = 2
    elif a > b:
        if (a - b) % 2 == 0:
            ans = 2
        else:
            ans = 1
    else:
        ans = 0
    print(ans)

# SOLUTION

import sys
import math

t = int(input())
for _ in range(t):
    a, b = map(int, sys.stdin.readline().split())
    if a == b:
        print(0)
    else:
        if a % 2 == 1:
            if b % 2 == 1:
                print(int(math.ceil(abs(a - b) / 2)))
            else:
                print(int(math.ceil(abs(a - b) / 2)) + 1)
        else:
            if b % 2 == 1:
                print(int(math.ceil(abs(a - b) / 2)) + 1)
            else:
                print(int(math.ceil(abs(a - b) / 2)))
