def main():
    a, b = map(int, input().split())
    m, sigma = map(int, input().split())
    maxR = 0
    for x in range(1, m//2+1):
        y = (sigma - 2*x)
        if y < 1:
            continue
        R = a*x + b*y
        if R > maxR:
            maxR = R
    print(maxR)

if __name__ == "__main__":
    main()

def solve(a, b, m, s):
    x = s - b
    if x < 0:
        x = 0
    else:
        x /= 2
    y = m - x
    if y < 0:
        y = 0
    return (a * x) + (b * y)

def main():
    a, b = [int(x) for x in raw_input().split()]
    m, s = [int(x) for x in raw_input().split()]
    print solve(a, b, m, s)

if __name__ == '__main__':
    main()

def main():
    a, b = map(int, input().split())
    m, sigma = map(int, input().split())

    if sigma > m:
        print(0)
    else:
        print(max(0, (m-sigma)*a + sigma*b//2))

if __name__ == '__main__':
    main()
