    if a[0] <= b[0] and b[1] <= a[1]:
        return a
    if b[0] <= a[0] and a[1] <= b[1]:
        return b
    if b[0] <= a[0] and a[0] <= b[1]:
        if a[1] <= b[1]:
            return [b[0], a[1]]
        else:
            return b
    if a[0] <= b[0] and b[0] <= a[1]:
        if b[1] <= a[1]:
            return [a[0], b[1]]
        else:
            return a
    return None

