def main():
    n = int(input())
    heights = list(map(int, input().split()))

    for i in range(1, n + 1):
        if heights[i - 1] > heights[i]:
            heights[i] += 1

    for i in range(1, n + 1):
        if heights[i - 1] > heights[i]:
            print('No')
            exit()
    else:
        print('Yes')


if __name__ == '__main__':
    main()
import sys

def main():
    n = int(sys.stdin.readline())
    heights = list(map(int, sys.stdin.readline().split()))
    if n == 1:
        print('Yes')

        return
    prev_h = heights[0]
    for i in range(1, n):
        curr_h = heights[i]
        if curr_h < prev_h:
            if curr_h == 0:
                print('No')
                return
            heights[i] -= 1
        prev_h = heights[i]
    print('Yes')
    return


if __name__ == '__main__':
    main()
