    if (H-h) == 0 or (W-w) == 0:
        return cnt
    elif (H-h) < 0 or (W-w) < 0:
        return cnt
    else:
        cnt += (H-h)*(W-w)
        return calc_remaining_cells(H-h, W-w, h, w, cnt)

