import math

def round_dances(n):
    return math.factorial(n) // (2 * math.factorial(n // 2))

if __name__ == '__main__':
    n = int(input())
    print(round_dances(n))

#Solution

n = int(input())

def factorial(n):
    if n == 0:
        return 1
    else:
        return n * factorial(n - 1)

print(factorial(n) // (factorial(n // 2) ** 2))
