def solve(arr):
    for i in range(len(arr)):
        if arr[i] != i+1:
            return i+1
    return len(arr)+1


if __name__ == "__main__":
    t = int(input())
    for _ in range(t):
        n = int(input())
        arr = list(map(int, input().split()))
        print(solve(arr))




import sys

def main():
    num_queries = int(sys.stdin.readline())
    for _ in range(num_queries):
        a, b, k = map(int, sys.stdin.readline().split())
        if k % 2:
            print(a * (k // 2) - b * (k // 2 + 1))
        else:
            print(a * (k // 2) - b * (k // 2))


if __name__ == "__main__":
    main()


