            if char not in curr.children:
                curr.children[char] = Node()
            curr.children[char].count += 1
            curr = curr.children[char]
        return curr

    def find(self, word):
        curr = self.root
        for char in word:
            if char in curr.children:
                curr = curr.children[char]
            else:
                return 0
        return curr.count


