from sys import stdin

t = int(stdin.readline().strip())

for _ in range(t):
    n, x, y = [int(x) for x in stdin.readline().strip().split()]
    if n == 2:
        print(x, y)
    elif n == 3:
        print(x, y, x+y)
    elif n == 4:
        print(x, y, x+y, x*y)
    else:
        if n % 2 == 0:
            print(x, y, x+y, (n-4)//2*x*y, x*y, x+y, (n-4)//2*x*y, x*y)
        else:
            print(x, y, x+y, (n-5)//2*x*y, x*y, x+y, (n-5)//2*x*y, x*y, x)

t = int(input())
for i in range(t):
    n,x,y = map(int,input().split())
    if 2*x==y:
        print(x,y,end=" ")
        for j in range(3,n+1):
            print(x+y,end=" ")
    else:
        print(x,y,end=" ")
        l = [x,y]
        d = (y-x)//(n-2)
        for j in range(n-2):
            l.append(l[-1]+d)
        for j in l:
            print(j,end=" ")
    print()
