class Solution:
    def reverseBits(self, n: int) -> int:
        rev = ''
        for i in reversed(bin(n)[2:]):
            rev = rev + i
        rev = rev + '0'*(32-len(rev)) 

        return int(rev, 2)

class Solution:
    def reverseBits(self, n: int) -> int:
        a = bin(n)[2:]
        z = "0"*(32 - len(a)) + a
        t = z[::-1]
        return int(t, 2)

class Solution:
     def reverseBits(self, n: int) -> int:
        result = 0
        power = 31
        while n:
            result += (n & 1) << power
            n = n >> 1
            power -= 1

        return result

