import sys
import math

def nimionese_word(word):
    vowels = ['a', 'e', 'i', 'o', 'u']
    consonants = ['b', 'c', 'd', 'g', 'k', 'n', 'p', 't']
    nimionese_word = ''

    # Replace the first letter with the nearest hard consonant
    if word[0] in consonants:
        nimionese_word += word[0]
    else:
        if word[0] in vowels:
            nimionese_word += 'b'
        else:
            nimionese_word += chr(ord(word[0]) - 1)

    for i in range(1, len(word)):
        if word[i] in consonants:
            if word[i] == 'h':
                word = word[:i] + word[i+1:]
            elif word[i] == 'p':
                word = word[:i] + 'b' + word[i+1:]
            elif word[i] == 't':
                word = word[:i] + 'd' + word[i+1:]
            elif word[i] == 'g':
                word = word[:i] + 'c' + word[i+1:]
        elif word[i] == 'e':
            word = word[:i] + 'dach' + word[i+1:]
    word = word.replace('-', '')
    nimionese_word += word[1:]

    # Add the ending
    if word[-1] not in consonants:
        nimionese_word += 'ah'
    else:
        if word[-1] == 'n':
            nimionese_word += 'ah'
        elif word[-1] == 'k':
            nimionese_word += 'oh'
        else:
            nimionese_word += 'uh'

    return nimionese_word

def main():
    sentence = sys.stdin.readline().strip()
    words = sentence.split(' ')

    nimionese_sentence = ''

    for word in words:
        nimionese_sentence += nimionese_word(word) + ' '

    print(nimionese_sentence[:-1])

if __name__ == '__main__':
    main()

import sys
import math

def main():
    # Get input
    sentence = sys.stdin.readline().strip().split()

    # Translate
    translated = []
    for word in sentence:
        translated.append(translate(word))

    # Print
    print(' '.join(translated))

def translate(word):
    # Get syllables
    syllables = word.split('-')

    # Translate
    for i in range(len(syllables)):
        syllables[i] = translate_syllable(syllables[i])

    return ''.join(syllables)

def translate_syllable(syllable):
    # First letter
    if syllable[0] in 'acegikmoqsuwy':
        if syllable[0] < 'n':
            syllable = syllable.replace(syllable[0], 'b', 1)
        else:
            syllable = syllable.replace(syllable[0], 'p', 1)

    # Each
    if syllable == 'each':
        syllable = 'dach'

    # Subsequent consonants
    if len(syllable) > 1:
        for i in range(1, len(syllable)):
            if syllable[i] in 'bcdfghjklmnpqrstvwxz':
                syllable = syllable.replace(syllable[i], syllable[0], 1)

    # End consonant
    if syllable[-1] in 'bcdfghjklmnpqrstvwxz':
        if syllable[-1] < 'n':
            syllable += 'ah'
        elif syllable[-1] < 't':
            syllable += 'oh'
        else:
            syllable += 'uh'

    return syllable

if __name__ == '__main__':
    main()


