        return '%d-%d=%d' % (a, b, c)
    if a * b == c:
        return '%d*%d=%d' % (a, b, c)
    if a == b * c:
        return '%d=%d*%d' % (a, b, c)
    if a == b / c:
        return '%d=%d/%d' % (a, b, c)
    if a / b == c:
        return '%d/%d=%d' % (a, b, c)
    return 'No solution'
