import sys

def main():
    events = []
    for line in sys.stdin:
        args = line.split()
        events.append(args)

    shares_owned = 0
    share_cost = 0.0
    for event in events:
        if event[0] == "buy":
            shares_owned += int(event[1])
            share_cost = (share_cost * (shares_owned - int(event[1])) + int(event[1]) * float(event[2])) / shares_owned
        elif event[0] == "sell":
            shares_owned -= int(event[1])
        elif event[0] == "split":
            shares_owned *= int(event[1])
            share_cost /= int(event[1])
        elif event[0] == "merge":
            shares_owned = (shares_owned // int(event[1])) + (shares_owned % int(event[1]))
            share_cost *= int(event[1])
        elif event[0] == "die":
            share_cost = float(event[1])
            break

    print(shares_owned * share_cost * (1 - 0.3))  # 0.3 is the tax rate

main()


import sys

def main():
    # Reading in the input
    lines = sys.stdin.readlines()
    lines = [line.strip() for line in lines]
    # Initializing variables
    num_shares = 0
    cost_per_share = 0
    # Iterating through the events
    for line in lines:
        event = line.split()
        # Buy event
        if event[0] == 'buy':
            num_shares += int(event[1])
            cost_per_share += int(event[1]) * float(event[2])
        # Sell event
        elif event[0] == 'sell':
            num_shares -= int(event[1])
            cost_per_share -= int(event[1]) * float(event[2])
        # Split event
        elif event[0] == 'split':
            num_shares *= int(event[1])
            cost_per_share /= int(event[1])
        # Merge event
        elif event[0] == 'merge':
            # Calculate how many shares will be merged
            num_shares_to_merge = num_shares - (num_shares % int(event[1]))
            num_shares -= num_shares_to_merge
            cost_per_share = (cost_per_share * num_shares_to_merge) / float(event[1])
            num_shares += 1
        # Die event
        elif event[0] == 'die':
            # Calculate the final sum after taxes
            final_sum = num_shares * (float(event[1]) - (0.3 * (float(event[1]) - cost_per_share / num_shares)))
            print(final_sum)

if __name__ == '__main__':
    main()


def main():
    shares = []
    for i in range(int(input())):
        temp = input().split()
        if temp[0] == "buy":
            shares.append(int(temp[1]) * int(temp[2]))
        if temp[0] == "sell":
            shares.sort()
            for j in range(int(temp[1])):
                shares[j] = int(temp[2]) - shares[j]
        if temp[0] == "split":
            temp2 = []
            for j in shares:
                temp2.append(j/int(temp[1]))
            shares = temp2
        if temp[0] == "merge":
            temp2 = []
            for j in range(int(len(shares)/int(temp[1]))):
                temp2.append(sum(shares[j*int(temp[1]):(j+1)*int(temp[1])]))
            if len(shares)%int(temp[1]) != 0:
                temp2.append(sum(shares[int(len(shares)/int(temp[1]))*int(temp[1]):]))
            shares = temp2
        if temp[0] == "die":
            shares = [int(temp[1]) - i for i in shares]
    print(sum(shares)/len(shares))

main()
