import sys

def is_diverse(s):
    if len(s) == 1:
        return True
    if len(s) == len(set(s)):
        if ord(s[0]) + 1 == ord(s[1]):
            return is_diverse(s[1:])
        else:
            return False
    else:
        return False

def main():
    n = int(sys.stdin.readline())
    for i in range(n):
        s = sys.stdin.readline().strip()
        if is_diverse(s):
            print("Yes")
        else:
            print("No")

if __name__ == '__main__':
    main()

