def solve(n, a):
    ans = 1
    for i in range(1, n):
        if a[i] > a[i - 1]:
            ans += 1
        else:
            break
    for i in range(n - 2, -1, -1):
        if a[i] < a[i + 1]:
            ans += 1
        else:
            break
    return ans

def main():
    n = int(input())
    a = list(map(int, input().split()))
    print(solve(n, a))

if __name__ == "__main__":
    main()

def solve(n, a):
    max_len = 1
    curr_len = 1
    for i in range(1, n):
        if a[i] > a[i-1]:
            curr_len += 1
        else:
            max_len = max(max_len, curr_len)
            curr_len = 1
    return max(max_len, curr_len)

if __name__ == '__main__':
    n = int(input())
    a = list(map(int, input().split()))
    print(solve(n, a))
