def sum_of_digits(n):
    return sum(int(i) for i in str(n)) % 4 == 0

def next_interesting(n, i=1):
    while not sum_of_digits(n):
        n += i
        i += 1
    return n - i + 1

def main():
    n = int(input())
    print(next_interesting(n))

if __name__ == "__main__":
    main()

def sum_digits(n):
    s = 0
    while n:
        s += n % 10  # modulo
        n //= 10  # integer division

    return s

def main():
    a = int(input())
    while sum_digits(a) % 4 != 0:
        a += 1
    print(a)


if __name__ == '__main__':
    main()


