def main():
    n, k = map(int, input().split())
    if k == 0:
        print(n)
        return
    s = input()
    cost = 0
    for i in range(n):
        if s[i] == '0':
            cost += i + 1
        else:
            cost += min(i + 1, n - i)
    print(cost)

if __name__ == "__main__":
    main()


def main():
    n, k = map(int, input().split())
    s = input()
    s = list(map(int, s))
    ans = 0
    for i in range(n):
        if s[i] == 0:
            ans += i + 1
    for i in range(n):
        if s[i] == 1:
            ans += i + 1
            for j in range(i-k, i+k+1):
                if j >= 0 and j < n:
                    s[j] = 0
    print(ans)

if __name__ == '__main__':
    main()

