def main():
    n = int(input())
    skills = list(map(int, input().split()))
    skills.sort()
    max_team_size = 1
    for i in range(n - 1):
        if skills[i + 1] - skills[i] <= 5:
            max_team_size += 1
    print(max_team_size)

if __name__ == '__main__':
    main()


def main():
    n = int(input())
    a = [int(x) for x in input().split()] 
    a.sort() 
    ans = 1 
    for i in range(1, n): 
        if a[i] - a[i-1] <= 5: 
            ans += 1 
    print(ans) 


