import sys

def median(a, l, r):
    if (r - l) % 2 == 0:
        return a[(l + r) // 2]
    else:
        return a[(l + r) // 2 + 1]

def main():
    n, m = map(int, sys.stdin.readline().split())
    a = list(map(int, sys.stdin.readline().split()))

    a.sort()

    count = 0
    for i in range(n):
        for j in range(i, n):
            if median(a, i, j) == m:
                count += 1

    print(count)

if __name__ == "__main__":
