n, k, q = map(int, input().split())
a = [int(input()) for _ in range(q)]

p = [k] * n  # 各人が持っている枚数
for i in range(q):
    p[a[i] - 1] += 1

for i in range(n):
    if p[i] > 0:
        print("Yes")
    else:
        print("No")

n, k, q = map(int, input().split())
a = [int(input()) for _ in range(q)]

p = [k] * n
for i in range(q):
    for j in range(n):
        if j != a[i] - 1:
            p[j] -= 1

for i in range(n):
    if p[i] > 0:
        print("Yes")
    else:
        print("No")

n, k, q = map(int, input().split())
a = [int(input()) for _ in range(q)]

players = [k] * n

for i in range(q):
    for j in range(n):
        if j + 1 != a[i]:
            players[j] -= 1

for i in range(n):
    if players[i] <= 0:
        print("No")
    else:
        print("Yes")
