    if i == M and '-' not in s:
        ans.append(s)
        return
    for j in range(M):
        if s[c[j][0]-1] == str(c[j][1]):
            continue
        if s[c[j][0]-1] == '-':
            s[c[j][0]-1] = str(c[j][1])
            rec(i+1, s, c, ans)
            s[c[j][0]-1] = '-'
        else:
            s[c[j][0]-1] = str(c[j][1])
            rec(i+1, s, c, ans)
            s[c[j][0]-1] = '-'

