    s = ['-' for i in range(N)]
    c = []
    for i in range(M):
        c.append(list(map(int, input().split())))
    ans = []
    rec(0, s, c, ans)
    if ans == []:
        print(-1)
    else:
        print(''.join(ans[0]))

