n = int(input())
h = list(map(int, input().split()))

ans = 0

for i in range(1, n):
    if h[i - 1] >= h[i]:
        ans += 1
    else:
        h[i] = h[i - 1]

print(ans)

N = int(input())
H = list(map(int, input().split()))

count = 0
for i in range(N - 1):
    if H[i] >= H[i + 1]:
        count += 1
    else:
        count = 0
print(count + 1)

N = int(input())
H = list(map(int, input().split()))

ans = 0
for i in range(1, N):
    if H[i - 1] >= H[i]:
        ans += 1
    elif H[i - 1] == H[i] - 1:
        H[i] -= 1
        ans += 1
print(ans)
